mapper.insert:
 		insert  
 			into tb_calendar
 			(
			  calendar_no 			
 			, user_no
 			, schedule_title
 			, start_date
 			, end_date
 			, date_color
 			, schedule_content
 			, category
 			, text_color
 			, all_day  
 			)
 			values
 			(
 			  seq_calendar.nextval
 			, #{userNo}
 			, #{title}
 			, #{start}
 			, #{end}
 			, #{backgroundColor}
 			, #{description}
 			, #{type}
 			, #{textColor}
 			, #{allDay} 			 
 			)

mapper.update:
update
	  tb_calendar
set start_date = #{start}
  , end_date = #{end}
  where calendar_no = #{_id}
