mapper.update:
update
	tb_chat_user
set count = count + 1
where room_no = #{roomNo}
and user_no != #{userNo}

update
	tb_chat_user
set count = 0
where room_no = #{roomNo}
	and user_no = #{userNo}

mapper.select:
select *
from tb_chat_user
where user_no = #{userNo}
	and room_no = #{roomNo}

mapper.delete:
delete 
	from tb_chat_user 
  where user_no = #{userNo}
  	and room_no = #{roomNo}

mapper.update:
update
	tb_chat_user
set status = 'H' 
where room_no = #{roomNo}
	and user_no = #{userNo}

update
	tb_chat_user
set status = 'S' 
where room_no = #{roomNo}
	and user_no = #{userNo}
