mapper.insert:
insert 
    into tb_member
    (	user_no
      , user_id
      , user_pwd
      , user_name
      , gender
      , email
      , profile
      , social
    )
    values
    (
    	seq_member.nextval
      , #{userId}
      , #{userPwd}
      , #{userName}
      , #{gender}
      , #{email}  
      , #{profile}
      , #{social}
    )

mapper.select:
select 
	    count(*) as "count"
  from tb_member
 where user_id = #{userId}
   and email = #{email}

mapper.update:
update tb_member
   set token = #{token}
 where user_no = #{userNo}

update tb_member
   set connect_status = 'Y'
 where user_no = #{no}
