mapper.insert:
insert 
	into tb_news_comments
	(
		news_no
	  , comment_writer
	  , comment_content
	)
	values
	(
		#{newsNo}
	  , #{commentWriter}
	  , #{commentContent}
	)

insert
  into tb_news
  (
     news_no
   , user_no
   , news_title
   , news_content
   , thumbnail
  )
  values
  (
     seq_news.nextval
   , #{userNo}
   , #{newsTitle}
   , #{newsContent}
   , #{thumbnail}
  )

mapper.update:
update tb_news
	set status = 'N'
	where news_no = #{newsNo}

mapper.select:
select count(*) as "count"
	from tb_news_zzim
	where news_no = #{newsNo}
	and user_no = #{userNo}

select count(*) as "count"
	from tb_news_zzim
	where news_no = #{newsNo}
	and user_no = #{userNo}
	and status = 'Y'

mapper.update:
  update tb_news_zzim
	  set status = 'Y'
	  where news_no = #{newsNo}
	  and user_no =#{userNo}

  update tb_news_zzim
	  set status = 'N'
	  where news_no = #{newsNo}
	  and user_no =#{userNo}

update tb_news
   set news_title = #{newsTitle},
       news_content = #{newsContent},
       update_date = sysdate,
       thumbnail = #{thumbnail}
  where news_no = #{newsNo}
